<?php if (!defined('ABSPATH')) exit; get_header(); ?><main id="gsMain" class="gs-container gs-section" role="main"><?php if (have_posts()) : ?><h1 class="gs-h2"><?php echo esc_html(get_the_archive_title() ?: get_bloginfo('name')); ?></h1><div style="margin-top: var(--gs-step-6); display:grid; gap: var(--gs-step-4);"><?php while (have_posts()) : the_post(); ?><article class="gs-card"><div class="gs-card__title"><span class="gs-icon" aria-hidden="true"><?php echo gs_inline_svg('default'); ?></span><a href="<?php the_permalink(); ?>"><?php the_title(); ?></a></div><div class="gs-card__desc"><?php echo esc_html(wp_strip_all_tags(get_the_excerpt() ?: '')); ?></div></article><?php endwhile; ?></div><?php else : ?><h1 class="gs-h2"><?php echo esc_html__('Пока нет записей', 'global-service'); ?></h1><?php endif; ?></main><?php get_footer(); ?>