<?php if (!defined('ABSPATH')) exit; get_header(); ?><main id="gsMain" class="gs-container gs-section" role="main"><div class="gs-kicker"><?php echo esc_html__('Поиск', 'global-service'); ?></div><h1 class="gs-h2" style="margin-top:10px;"><?php echo esc_html__('Результаты поиска', 'global-service'); ?></h1><?php if (have_posts()) : ?><div style="margin-top: var(--gs-step-6); display:grid; gap: var(--gs-step-4);"><?php while (have_posts()) : the_post(); ?><article class="gs-card"><div class="gs-card__title"><span class="gs-icon" aria-hidden="true"><?php echo gs_inline_svg('default'); ?></span><a href="<?php the_permalink(); ?>"><?php the_title(); ?></a></div><div class="gs-card__desc"><?php echo esc_html(wp_strip_all_tags(get_the_excerpt() ?: '')); ?></div></article><?php endwhile; ?></div><?php else : ?><p class="gs-muted" style="margin-top:14px;"><?php echo esc_html__('Ничего не найдено.', 'global-service'); ?></p><?php endif; ?></main><?php get_footer(); ?>